const Socket = io() 


function deleteCommand(id) {
 Socket.emit("DELETE_COMMAND", id)
 alert("Command is deleted!")
}


function AlertScreen(name, id) {
  const bool = confirm("Do you want to delete "+ name +"?")
  if (bool === true) {
    deleteCommand(id) 
    const ElementLmao = $(`#command-${id}`)
    ElementLmao.remove()
  } else {
    return;
  }
}