function deleteVar(name) {
  document.location.href = "/variables"
  Socket.emit("VARIABLES_REMOVE", name)
}

function AlertDelete(name) {
  const bool = confirm("Do you want to delete "+name + "?")
  if (bool) {
    deleteVar(name)
    alert("Deleted "+name)
  } else {
    return;
  }
}


const FORM = document.querySelector("#FormF")
const Socket = io() 

FORM.addEventListener("submit", function(k) {
  k.preventDefault()
  const L = {}
  L[$("#FormF").serializeArray().find(f => f.name === "name").value] = $("#FormF").serializeArray().find(f => f.name === "value").value

  const obj = {deleteVar:$("#FormF").serializeArray().find(f => f.name === "NOP").value, update: L}
  Socket.emit("VARIABLES_MODIFY", obj)
  document.location.href="/edit/variables/"+$("#FormF").serializeArray().find(f => f.name === "name").value
  const text = "<div id='savedtext' class='rounded-1 text-white p-2 mb-3' style='background-color:blueviolet;'><p style='font-size:1.3rem;'>Saved Successfully</p></div>"
  $("#FormF").prepend(text)
  window.setTimeout(function() {
    $("#savedtext").remove()
  }, 5000)
})