const banAddCommands = require("../handlers/banAddCommands") 

module.exports = async (client, guild, user) => {
	if (client.options.fetchInvites) {
		const data = await client.db.findOne({
			guild: guild.id,
			id: user.id
		})

		if (data && data.inviter.id.length) {
			const inviter = await client.db.findOne({
				guild: guild.id,
				id: data.inviter.id
			})

			inviter.real--
			inviter.fake++

			await client.db.set(inviter, {
				guild: guild.id,
				id: data.inviter.id
			})
		}
	}

  banAddCommands(client, guild, user)
}
