module.exports = async d => {
    const code = d.command.code

    const r = code.split("$addObjectProperty[").length - 1

    const inside = code.split("$addObjectProperty[")[r].split("]")[0]

    const [
        key,
        value
    ] = inside.split(";")

    if (!d.object) return d.error(`:x: No object present in \`$addObjectProperty[${inside}]\``)

    let result = ""
    
    try {
      result = Number(value) && Number(value) > Number.MAX_SAFE_INTEGER ? value : JSON.parse(value)
      
    } catch {
      result = value
    }

    d.object[key] = result

    return {
        object: d.object,
        code: code.replaceLast(`$addObjectProperty[${inside}]`, "")
    }
}