module.exports = async d => {
    const code = d.command.code 
    
    const r = code.split("$getChannelSlowmode").length - 1 
    
    const after = code.split("$getChannelSlowmode")[r] 
    
    if (after[0] === "[") {
        const inside = after.slice(1).split("]")[0] 
        
        const channel = d.client.channels.cache.get(inside) 
        
        if (!channel) return d.error(`‚ùå Invalid channel ID in \`$getChannelSlowmode[${inside}]\``) 
        
        return {
            code: code.replaceLast(`$getChannelSlowmode[${inside}]`, channel.rateLimitPerUser || 0)
        }
    } else {
        return {
            code: code.replaceLast(`$getChannelSlowmode`, d.message.channel.rateLimitPerUser || 0)
        }
    }
}