
const getServerVar = async d => {

	const code = d.command.code

	const r = code.split("$getServerVar[").length - 1

	const inside = code.split("$getServerVar[")[r].split("]")[0]

	let [variable, guildID] = inside.split(";")
	
	if (!guildID) guildID = d.message.guild ? d.message.guild.id : "" 
	
	if (d.client.variables[variable] === undefined) return d.error(`:x: Variable '${variable}' not found!`)

	let item = await d.client.db.findOne({
		variable: variable,
		varID: `${guildID}`
	})

	if (!item)
		item = { value: d.client.variables[variable] }

	return {
		code: code.replaceLast(`$getServerVar[${inside}]`, item.value)
	}
}

module.exports = getServerVar