module.exports = async d => {
    const code = d.command.code 
    
    const r = code.split("$giveRole[").length - 1 
    
    const inside = code.split("$giveRole[")[r].split("]")[0] 
    
    const [userID, roleID] = inside.split(";") 
    
    const s = await d.client.api.guilds(d.message.guild.id).members(userID).roles(roleID).put().catch(err => null)
    
    if (!s) return d.error(`‚ùå Failed to give role! `) 
    
    return {
        code: code.replaceLast(`$giveRole[${inside}]`, "")
    }
}
