module.exports = async d => {
    const code = d.command.code

    const r = code.split("$lowestRole").length - 1

    const after = code.split("$lowestRole")[r]

    if (after[0] === "[") {
        const inside = after.slice(1).split("]")[0]

        const member = await d.message.guild.members.fetch(inside).catch(err => {})

        if (!member) return d.error(`:x: Invalid user ID in \`$lowestRole[${inside}]\``)

        else return {
            code: code.replaceLast(`$lowestRole[${inside}]`, (member.roles.cache.sort((x, y) => x.position - y.position).array().slice(1)[0] || "").id || "")
        }
    } else {
        return {
            code: code.replaceLast(`$lowestRole`, (d.message.member.roles.cache.sort((x, y) => x.position - y.position).array().slice(1)[0] || "").id || "")
        }
    }
}