module.exports = async d => {
    const code = d.command.code

    const r = code.split("$memberExists[").length - 1

    const inside = code.split("$memberExists[")[r].split("]")[0]
    
    const [ userID, guildID = d.message.guild.id] = inside.split(";") 
    
    const guild = d.client.guilds.cache.get(guildID) 
    
    if (!guild) return d.error(`âŒ Invalid guild ID in \`$memberExists[${inside}]\``) 
    
    const member = await guild.members.fetch(userID || "none").catch(err => null)
    
    return {
        code: code.replaceLast(`$memberExists[${inside}]`, Boolean(member)) 
    }
}