module.exports = d => {
  
  let code = d.command.code
  
  const r = code.split("$noMentionMessage").length - 1
  
  const after = code.split("$noMentionMessage")[r]
  
  const args = d.args.join(" ").replace(/(<#(\d{17,19})>|<@!?(\d{17,19})>|<@&(\d{17,19})>)/g, "").trim().split(/ +/g) 
  
  if (after.startsWith("[")) {
    let n = after.split("[")[1].split("]")[0]
        
    let _ = n 
    if (["last", ">"].includes(n.addBrackets())) n = args.length  
    if (isNaN(n)) return d.error(`‚ùå Invalid number in \`$noMentionMessage[${n}]\``)
    
    code = code.replaceLast(`$noMentionMessage[${_}]`, args[Number(n) - 1] ? args[Number(n) - 1].deleteBrackets() : "")
  } else {
    code = code.replaceLast("$noMentionMessage", args.join(" ").deleteBrackets()) 
  }
  
  return {
    code: code 
  } 
}