module.exports = async d => {
    const code = d.command.code

    const r = code.split("$resetServerVar[").length - 1

    const inside = code.split("$resetServerVar[")[r].split("]")[0]

    const all = await d.client.db.find({
            variable: inside
    })

    if (d.client.variables[inside] === undefined) return d.error(`:x: Invalid variable '${inside}' in \`$resetServerVar[${inside}]\``)

		const deleted = []

    for (const data of all.filter(e => d.client.guilds.cache.has(e["varID"]))) {
        deleted.push({ _index: data['_index'] })
    }

		await d.client.db.deleteMany(...deleted)

    return {
        code: code.replaceLast(`$resetServerVar[${inside}]`, "")
    }
}