module.exports = async d => {
    const code = d.command.code

    const r = code.split("$resetUserVar[").length - 1

    const inside = code.split("$resetUserVar[")[r].split("]")[0]

    const all = await d.client.db.find({
            variable: inside
    })

    if (d.client.variables[inside] === undefined) return d.error(`:x: Invalid variable '${inside}' in \`$resetUserVar[${inside}]\``)

		const deleted = []

    for (const data of all.filter(e => e["varID"].includes(d.message.guild.id))) {
        deleted.push({ _index: data['_index'] })
    }

		await d.client.db.deleteMany(...deleted)

    return {
        code: code.replaceLast(`$resetUserVar[${inside}]`, "")
    }
}