module.exports = async d => {
    const code = d.command.code 
    
    const r = code.split("$roleMembersCount").length - 1 
    
    const inside = code.split("$roleMembersCount[")[r].split("]")[0] 
    
    const role = d.message.guild.roles.cache.get(inside) 
    
    if (!role) return message.channel.send(`❌ Invalid role ID in \`$roleMembersCount[${inside}]\``) 
    
    return {
        code: code.replaceLast(`$roleMembersCount[${inside}]`, role.members.size)
    }
}