module.exports = async d => {
    const code = d.command.code 
    
    const r = code.split("$takeRole[").length - 1 
    
    const inside = code.split("$takeRole[")[r].split("]")[0] 
    
    const [userID, roleID] = inside.split(";") 
    
    const s = await d.client.api.guilds(d.message.guild.id).members(userID).roles(roleID).delete().catch(err => null)
    
    if (!s) return d.error(`‚ùå Failed to remove role! `) 
    
    return {
        code: code.replaceLast(`$takeRole[${inside}]`, "")
    }
}
