const userInfo = require('../../utils/userInfo')
const defaultOpts = {
	inviter: {
		id: '',
		code: ''
	},
	real: 0,
	fake: 0
}

module.exports = async d => {
	let code = d.command.code

	const r = code.split('$userInfo').length - 1
	const inside = code.split('$userInfo')[r].after()
	
	if (!inside.inside) return d.error(`:x: Invalid usage in $userInfo${inside.total}`)

	const [ option, id = d.message.author.id ] = inside.splits

	if (!userInfo[option]) return d.error(`:x: Invalid option in \`$userInfo${inside.total}\``)

	const user = await d.client.users.fetch(id).catch(err => { })

	if (!user) return d.error(`:x: Invalid userID in \`$userInfo${inside.total}\``)

	const data = await d.client.db.findOne({
		guild: d.message.guild.id,
		id: user.id
	}) || defaultOpts

	return {
		code: code.replaceLast(`$userInfo${inside.total}`, eval(`data${userInfo[option].split(";")[1]}`))
	}
}