//Connecting to https://dbdjs.leref.ga
const ws = require("ws")
module.exports = function () {
  
const client = new ws("https://dbdjs.leref.ga/callbackStatus");
let ping

client.on("open", () => {
  console.log("\x1b[32mCLIENT\x1b[0m: Connected to https://dbdjs.leref.ga")
  client.send("\x1b[32mCLIENT\x1b[0m: Connected to Server")

  ping = setInterval(function() {
    client.ping()
  }, 1000 * 60)
})

client.on("close", (server, code, reason) => {
  console.log("Closed Connection from https://dbdjs.leref.ga \nSTATUS_CODE:"+code+"\nREASON:"+reason)
})

client.on("error", () => {
  console.log("Error has occured while connecting/connected/operating to API")
})


return client

}